let mean xs = sum xs / length xs;

let standardDev avg xs =
   (sum (map (fun x -> (x - avg) ** 2) xs) / length xs - 1) ** 0.5;

let stats xs =
   let avg = mean xs
   in [avg, standardDev avg xs];
